import './App.css';
import { Component } from 'react';
import { PostCard } from './components/PostCard';
import { loadsPost } from './utilitarios/load-post';

class App extends Component{


  constructor(props){
    super(props);
    this.state = {
      posts: []
    };

  }

  componentDidMount(){

    this.loadsPost();

  }


  loadsPost = async () => {

    const postsAndPhotos = await loadsPost();

    this.setState({ posts: postsAndPhotos })
  }

  render(){

    const { posts } = this.state;

    return (

      <section className="container">
      <div className="posts">
        {posts.map(post => (
          <PostCard 
              key={post.id}
              title={post.title}
              id={post.id}
              body={post.body}
              cover={post.cover}
          />
        ))}
      </div>
      </section>
    );
  }

  }

  export default App;
